package com.github.quartz.examples.config;import com.alibaba.druid.pool.DruidDataSource;import org.springframework.boot.autoconfigure.jdbc.DataSourceBuilder;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import javax.sql.DataSource;/** * Created by chenmin on 17/11/19. */@Configurationpublic class DataSourceConfiguration {    @Bean    @ConfigurationProperties(prefix = "spring.datasource")    public DataSource dataSource() {        return DataSourceBuilder.create().type(DruidDataSource.class).build();    }}