package com.github.quartz.http;import com.github.quartz.config.QuartzRestTemplateProperties;import com.github.quartz.model.entity.QrtzTimedTask;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.InitializingBean;import org.springframework.util.ObjectUtils;import org.springframework.web.client.RestTemplate;/** * @DESCRIPTION: Quartz远程调用客户端 * @AUTHER: chenmin * @CREATE BY: 18/1/13 上午12:50 */public class ScheduleExecutorClient implements InitializingBean {    public static final Logger logger = LoggerFactory.getLogger(ScheduleExecutorClient.class);    private RestTemplate restTemplate;    private QuartzRestTemplateProperties quartzRestemplateProperties;    public ScheduleExecutorClient setRestTemplate(RestTemplate restTemplate) {        this.restTemplate = restTemplate;        return this;    }    public ScheduleExecutorClient setQuartzRestemplateProperties(QuartzRestTemplateProperties quartzRestemplateProperties) {        this.quartzRestemplateProperties = quartzRestemplateProperties;        return this;    }    public void invoke(QrtzTimedTask qrtzTimedTask) {    }    public <T> T invoke(String uri, Object param, Class<T> responseType) {        String url = uri + quartzRestemplateProperties.getApi();        return restTemplate.postForObject(uri, param, responseType);    }    @Override    public void afterPropertiesSet() throws Exception {        if (ObjectUtils.isEmpty(quartzRestemplateProperties)) {            logger.warn(quartzRestemplateProperties + " is not inited");        }        if (ObjectUtils.isEmpty(quartzRestemplateProperties.getApi()) || ObjectUtils.isEmpty(quartzRestemplateProperties.getUris())) {            logger.warn("ScheduleExecutorClient params missed");        }        if (ObjectUtils.isEmpty(restTemplate)) {            logger.error("restTemplate can not be null");        }    }}