package com.github.quartz.http;import com.github.quartz.model.entity.QrtzTimedTask;import com.github.quartz.utils.QuartzUtil;import org.springframework.context.ApplicationContext;import org.springframework.util.ReflectionUtils;import java.lang.reflect.Method;import java.util.Map;/** * @DESCRIPTION: * @AUTHER: chenmin * @CREATE BY: 18/1/13 上午12:35 */public class ScheduleExecutorImpl implements ScheduleExecutor {    private ApplicationContext applicationContext;    @Override    public void invoke(QrtzTimedTask qrtzTimedTask) {        String taskMethod = qrtzTimedTask.getTaskMethod();        String beanName = QuartzUtil.buildTargetObject(qrtzTimedTask);        Map<String, Object> param = QuartzUtil.buildParams(qrtzTimedTask);        Object bean = applicationContext.getBean(beanName);        Method method = ReflectionUtils.findMethod(bean.getClass(), taskMethod, Map.class);        ReflectionUtils.invokeMethod(method, bean, param);    }    public ScheduleExecutorImpl setApplicationContext(ApplicationContext applicationContext) {        this.applicationContext = applicationContext;        return this;    }}