package com.github.quartz.utils;import javax.servlet.http.HttpServletRequest;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.io.PrintWriter;import java.net.InetAddress;import java.net.UnknownHostException;/** * @DESCRIPTION: 网络相关工具 * @AUTHER: chenmin * @CREATE BY: 18/1/14 下午3:51 */public class HttpUtil {    /**     * 获取系统IP地址     * @return     */    public static String getIpAddress() {        try {            InetAddress inetAddress = InetAddress.getLocalHost();            return inetAddress.getHostAddress();        } catch (UnknownHostException e) {            return "";        }    }    public static void close(PrintWriter pw) {        if (pw != null) {            pw.flush();            pw.close();        }    }    public static String getRequestBody(HttpServletRequest request, String Charset) throws IOException {        StringBuilder sb = new StringBuilder();        try (BufferedReader br = new BufferedReader(new InputStreamReader(request.getInputStream(), Charset))) {            String line;            while ((line = br.readLine()) != null) {                sb.append(line);            }        } catch (IOException e) {            throw e;        }        return sb.toString();    }}