package com.quartz.utils;import org.springframework.util.ReflectionUtils;import java.lang.reflect.Field;import java.lang.reflect.Method;/** * Created by chenmin on 17/11/19. */public class ClassUtil {    public static String getClassName(String classPath) {        return classPath.substring(0, 1).toLowerCase() + classPath.substring(1);    }    public static Object getFieldValue(Object object, String fieldName) {        Class<?> clazz = object.getClass();        String get = "get" + fieldName.substring(0, 1).toUpperCase() + fieldName.substring(1);        Method getter = ReflectionUtils.findMethod(clazz, get);        if (getter == null) {            Field field = ReflectionUtils.findField(clazz, fieldName);            return ReflectionUtils.getField(field, fieldName);        } else {            return ReflectionUtils.invokeMethod(getter, object);        }    }}